package br.usp.pcs.dao;import javax.persistence.EntityManager;import javax.persistence.EntityManagerFactory;import javax.persistence.Persistence;import br.com.caelum.vraptor.ioc.Component;import br.com.caelum.vraptor.ioc.RequestScoped;@RequestScoped@Componentpublic final class EMF {    private static final EntityManagerFactory emfInstance =        Persistence.createEntityManagerFactory("transactions-optional");    private EntityManager entityManager;    public EMF() {        entityManager = emfInstance.createEntityManager();    }    public EntityManager getEntityManager() {        return entityManager;    }}